import React, { useState } from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import {LoginForm} from './LoginForm'

const Navigation = ({isLoggedIn}) => {
 // const [isLoggedIn, setIsLoggedIn] = useState(false); // Dodaj stan do śledzenia stanu zalogowania

  // Logika wylogowywania
  const handleLogout = () => {
    // Dodaj logikę do wylogowywania
  //  setIsLoggedIn(false);
  };

  return (
    <Navbar bg="dark" variant="dark" className='sticky-top'>
      <Navbar.Brand href="/home">Fret Ascend</Navbar.Brand>
      <Nav className="me-auto">
        <Nav.Link href="#articles">Artykuły</Nav.Link>
        <Nav.Link href="/chords">Akordy i Tabulatury</Nav.Link>
        <Nav.Link href="#video">Lekcje Wideo</Nav.Link>
        <Nav.Link href="#contact">Forum Dyskusyjne</Nav.Link>
        <Nav.Link href="#premium">Premium</Nav.Link>
      </Nav>
      <Nav className="ms-auto">
        {isLoggedIn ? (
          <>
            <Nav.Link href="#profile"><span className="bi bi-person"></span> Profil</Nav.Link>
            <Nav.Link onClick={handleLogout}><span className="bi bi-box-arrow-right"></span> Wyloguj</Nav.Link>
          </>
        ) : (
          <>
            {/* Dodaj link do trasy logowania */}
            <Nav.Link href="/users/login"><span className="bi bi-box-arrow-in-right"></span> Zaloguj</Nav.Link>
            {/* Dodaj link do trasy rejestracji */}
            <Nav.Link href="/users/register"><span className="bi bi-person-plus"></span> Zarejestruj</Nav.Link>
          </>
        )}
      </Nav>
    </Navbar>
  );
};

export default Navigation;
