import React, {useState, useEffect} from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate} from 'react-router-dom';
import LoginForm from './components/LoginForm';
import RegistrationForm from './components/RegistrationForm';
import Start from './components/Start'
import Home from './components/Home';
import Chords from './components/Chords'
import Navigation from './components/Navbar';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const App = () => {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };
  const handleLogout = () => {
    // Dodaj logikę do wylogowywania, jeśli to konieczne
    setIsLoggedIn(false);
  };

  const PrivateRoute = ({ element }) => {
    const navigate = useNavigate();
    const [isInitialized, setIsInitialized] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
      const checkLoginStatus = () => {
        const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
        setIsLoggedIn(loggedIn);
        setIsInitialized(true)

        if (!loggedIn) {
          // Jeśli użytkownik nie jest zalogowany, przekieruj go na stronę logowania
          toast.error('Musisz być zalogowany, aby uzyskać dostęp.');
          navigate('/users/login');
        }
      };

      // Sprawdź status logowania po pierwszym renderowaniu
      checkLoginStatus();
    }, [navigate]);

    // Oczekuj, aż sprawdzimy status logowania przed zwróceniem elementu
    return isInitialized && isLoggedIn ? <>{element}</> : null;
  };

  return (
    <Router>
      <div>
      <Navigation isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Start />} />
        <Route path="/users/login" element={<LoginForm handleLogin={handleLogin} />} />
        <Route path="/users/register" element={<RegistrationForm />} />
        <Route path="/home" element={<PrivateRoute element={<Home />} />} />
        <Route path="/chords" element={<PrivateRoute element={<Chords />} />} />
        {/* Inne trasy */}
      </Routes>
      <ToastContainer />
      </div>
    </Router>
  );
};

export default App;
