import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardGroup } from 'react-bootstrap';
import './Styles.css'

const Chords = () => {
  const [chords, setChords] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/chords');
        setChords(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Akordy i Tabulatury</h1>
      <CardGroup>
        {chords.map((chord) => (
          <Card key={chord.id}>
            <Card.Img variant="top" src={chord.imageUrl} />
            <Card.Body>
              <Card.Title>{chord.name}</Card.Title>
              <Card.Text>Opis akordu, jeśli jest dostępny</Card.Text>
            </Card.Body>
            <Card.Footer>
              <small className="text-muted">
                <audio controls>
                  <source src={chord.soundUrl} type="audio/mpeg" />
                  Your browser does not support the audio element.
                </audio>
              </small>
            </Card.Footer>
          </Card>
        ))}
      </CardGroup>
    </div>
  );
};

export default Chords;
