// App.js - główny plik routingu
import React, {useState} from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import RegistrationForm from './components/RegistrationForm';
import Start from './components/Start'
import Home from './components/Home';
import Chords from './components/Chords'
import Navigation from './components/Navbar';
//import routes from './components/routes'; // Zaimportuj zdefiniowane trasy


const App = () => {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };
  const handleLogout = () => {
    // Dodaj logikę do wylogowywania, jeśli to konieczne
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div>
      <Navigation isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Start />} />
        <Route path="/users/login" element={<LoginForm handleLogin={handleLogin} />} />
        <Route path="/users/register" element={<RegistrationForm />} />
        <Route path="/home" element={<Home />} />
        <Route path="/chords" element={<Chords />} />
        {/* Inne trasy */}
      </Routes>
      </div>
    </Router>
  );
};

export default App;
