import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardGroup } from 'react-bootstrap';
import './Styles.css'

const Chords = () => {
  const [chords, setChords] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/chords');
        setChords(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className='b-ground'>
      <div className="chords-container">
        <h1 className="center-title">Akordy i Tabulatury</h1>
  
        <CardGroup>
          {chords.map((chord) => (
            <Card key={chord.id} className="chord-card">
              <div className="center-content">
                <Card.Img variant="top" src={chord.imageUrl} className='chord-card-img' />
                <Card.Body>
                  <Card.Title className="custom-card-title">{chord.name}</Card.Title>
                </Card.Body>
                <Card.Body >
                  <small className='chord-audio-container'>
                    <audio controls  className='chord-audio'>
                      <source src={chord.soundUrl} type="audio/mpeg" />
                      Your browser does not support the audio element.
                    </audio>
                  </small>
                </Card.Body>
              </div>
            </Card>
          ))}
        </CardGroup>
      </div>
    </div>
  );
};

export default Chords;
