import React, { useState, useEffect, useContext, createContext } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import RegistrationForm from './components/RegistrationForm';
import Start from './components/Start';
import Home from './components/Home';
import Chords from './components/Chords';
import Navigation from './components/Navbar';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const NavigateContext = createContext();
export const IsLoggedInContext = createContext(); // Dodaj nowy context

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.setItem('isLoggedIn', 'false');
  };

  return (
    <Router>
      <NavigateContext.Provider value={navigate}>
        <IsLoggedInContext.Provider value={isLoggedIn}> {/* Dodaj nowy context */}
          <div>
            <Navigation isLoggedIn={isLoggedIn} handleLogout={handleLogout} />
            <Routes>
              <Route path="/" element={<Start />} />
              <Route path="/users/login" element={<LoginForm handleLogin={handleLogin} />} />
              <Route path="/users/register" element={<RegistrationForm />} />
              <Route path="/home" element={<PrivateRoute element={<Home />} />} />
              <Route path="/chords" element={<PrivateRoute element={<Chords />} />} />
              {/* Inne trasy */}
            </Routes>
            <ToastContainer />
          </div>
        </IsLoggedInContext.Provider>
      </NavigateContext.Provider>
    </Router>
  );
};

const PrivateRoute = ({ element }) => {
  const navigate = useContext(NavigateContext);
  const isLoggedIn = useContext(IsLoggedInContext); // Użyj nowego contextu

  useEffect(() => {
    const checkLoginStatus = () => {
      if (!isLoggedIn) {
        // Jeśli użytkownik nie jest zalogowany, przekieruj go na stronę logowania
        toast.error('Musisz być zalogowany, aby uzyskać dostęp.');
        navigate('/users/login');
      }
    };

    // Sprawdź status logowania przy każdym renderowaniu trasy prywatnej
    checkLoginStatus();
  }, [navigate, isLoggedIn]);

  return isLoggedIn ? element : null;
};

export default App;
