import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import RegistrationForm from './components/RegistrationForm';
import Start from './components/Start';
import Home from './components/Home';
import Chords from './components/Chords';
import Navigation from './components/Navbar';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [user, setUser] = useState(() => {
    // Początkowa wartość stanu na podstawie danych w localStorage
    const userData = localStorage.getItem('userData');
    return userData ? JSON.parse(userData) : null;
  });

  const handleLogin = (userData) => {
    setUser(userData);
    // Zapisz dane użytkownika w localStorage
    localStorage.setItem('userData', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    // Wyczyść dane użytkownika z localStorage przy wylogowywaniu
    localStorage.removeItem('userData');
  };

  const PrivateRoute = ({ element }) => {
    const navigate = useNavigate();
    const [isInitialized, setIsInitialized] = useState(false);

    useEffect(() => {
      const checkLoginStatus = () => {
        const userData = localStorage.getItem('userData');
        setIsInitialized(true);

        if (!userData) {
          toast.error('Musisz być zalogowany, aby uzyskać dostęp.');
          navigate('/users/login');
        }
      };

      // Sprawdź status logowania po pierwszym renderowaniu
      checkLoginStatus();
    }, [navigate]);

    // Oczekuj, aż sprawdzimy status logowania przed zwróceniem elementu
    return isInitialized && user ? <>{element}</> : null;
  };

  return (
    <Router>
      <div>
        <Navigation isLoggedIn={!!user} handleLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Start />} />
          <Route path="/users/login" element={<LoginForm handleLogin={handleLogin} />} />
          <Route path="/users/register" element={<RegistrationForm />} />
          <Route path="/home" element={<PrivateRoute element={<Home />} />} />
          <Route path="/chords" element={<PrivateRoute element={<Chords />} />} />
          {/* Inne trasy */}
        </Routes>
        <ToastContainer />
      </div>
    </Router>
  );
};

export default App;
