import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Styles.css';

const Articles = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    // Tutaj możesz dodać logikę pobierania danych artykułów, np. z API
    // W przykładzie użyłem pustej tablicy jako stanu artykułów

    // const fetchArticles = async () => {
    //   try {
    //     const response = await fetch('URL_API/articles');
    //     const data = await response.json();
    //     setArticles(data);
    //   } catch (error) {
    //     console.error('Błąd pobierania artykułów:', error);
    //   }
    // };

    // fetchArticles();
  }, []);

  return (
    <div className="b-ground-a d-flex justify-content-center align-items-center">
      <div>
        <h2>Lista artykułów</h2>
        {articles.length === 0 ? (
          <p>Brak dostępnych artykułów.</p>
        ) : (
          <ul>
            {articles.map((article) => (
              <li key={article.id}>
                <h3>{article.title}</h3>
                <p>{article.content}</p>
                {/* Dodatkowe informacje o artykule, np. data publikacji, autor, itp. */}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Articles;
