// LoginForm.js - Komponent formularza logowania
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Styles.css';


const LoginForm = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      console.log(data); // Obsługa odpowiedzi serwera

      if (data.token) {
        // Przekieruj na stronę Home
        navigate('/Home');
        localStorage.setItem('isLoggedIn', 'true');
        setIsLoggedIn(true);
      }
    } catch (error) {
      console.error('Błąd logowania:', error);
    }
  };

  return (
    <div className="bg-image text-white">
      <center>
        <br></br>
        <h2>Logowanie</h2>
        <form className="form-horizontal" onSubmit={handleLogin}>
          <div className="form-group">
            <label className="control-label col-sm-2" htmlFor="email">Email:</label>
            <div className="col-sm-30">
              <input
                type="email"
                className="form-control"
                id="email"
                placeholder="Wpisz adres email"
                name="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
          </div>
          <div className="form-group">
            <label className="control-label col-sm-2" htmlFor="pwd">Hasło:</label>
            <div className="col-sm-50">
              <input
                type="password"
                className="form-control"
                placeholder="Wpisz hasło"
                name="pwd"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>
          <div className="form-group">
            <div className="col-sm-offset-2 col-sm-10">
              <button type="submit" className="btn btn-info">Zaloguj</button>
            </div>
          </div>
        </form>
      </center>
    </div>
  );
};

export default LoginForm
