import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardGroup } from 'react-bootstrap';
import './Styles.css'

const Chords = () => {
  const [chords, setChords] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/chords');
        setChords(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div><center><h1>Akordy i Tabulatury</h1></center>
   <div className='b-ground'>          
      <CardGroup>
        {chords.map((chord) => (
          <Card key={chord.id} className="chord-card" >
            <center><Card.Img variant="top" src={chord.imageUrl} className='chord-card-img'/></center>
            <Card.Body>
              <Card.Title className="custom-card-title"><center>{chord.name}</center></Card.Title>
            </Card.Body>
            <Card.Footer className="chord-card-footer">
              <small className="text-muted">
                <audio controls className='chord-audio'>
                  <source src={chord.soundUrl} type="audio/mpeg" />
                  Your browser does not support the audio element.
                </audio>
              </small>
            </Card.Footer>
          </Card>
        ))}
      </CardGroup>
      </div>
      </div> 
  );
};

export default Chords;
