import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { AuthProvider, useAuth } from './AuthContext';
import LoginForm from './components/LoginForm';
import RegistrationForm from './components/RegistrationForm';
import Start from './components/Start';
import Home from './components/Home';
import Chords from './components/Chords';
import Navigation from './components/Navbar';

const PrivateRoute = ({ element }) => {
  const { user } = useAuth();

  if (!user) {
    // Jeśli użytkownik nie jest zalogowany, wyświetl komunikat Toast i przekieruj go na stronę startową
    toast.error('Musisz być zalogowany, aby uzyskać dostęp.');
    return <Navigate to="/" />;
  }

  return element;
};

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <div>
          <Navigation />
          <Routes>
            <Route path="/" element={<Start />} />
            <Route path="/users/login" element={<LoginForm />} />
            <Route path="/users/register" element={<RegistrationForm />} />
            <Route path="/home" element={<PrivateRoute element={<Home />} />} />
            <Route path="/chords" element={<PrivateRoute element={<Chords />} />} />
          </Routes>
          <ToastContainer />
        </div>
      </Router>
    </AuthProvider>
  );
};

export default App;
