import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Styles.css';

const ChordsForm = ({ onChordAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    imageUrl: '',
    soundUrl: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3000/chords', formData);
      const newChord = response.data;
      onChordAdded(newChord);
      setFormData({ name: '', imageUrl: '', soundUrl: '' }); // Wyczyszczenie formularza po dodaniu
    } catch (error) {
      console.error('Błąd dodawania akordu:', error);
    }
  };

  return (
    <form className="form-horizontal" onSubmit={handleSubmit}>


<div className="form-group">
          





      <div className="form-group">
      <label className="control-label col-sm-2" htmlFor="name">Nazwa akordu</label>
      <div className="col-sm-30">
      <input
          type="text"
          className="form-control"
          placeholder="Wpisz nazwę akordu"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>
        </div>
        
        <div className="form-group">
      <label className="control-label col-sm-2" htmlFor="imageUrl">URL Obrazka</label>
      <div className="col-sm-30">
        <input
          type="text"
          placeholder="Wklej URL obrazka"
          name="imageUrl"
          value={formData.imageUrl}
          onChange={handleChange}
          required
        />
      </div>
        </div>
        <div className="form-group">
      <label className="control-label col-sm-2" htmlFor="soundUrl">URL Dźwięku</label>
      <div className="col-sm-30">
        <input
          type="text"
          placeholder="Wklej URL dźwięku"
          name="soundUrl"
          value={formData.soundUrl}
          onChange={handleChange}
          required
        />
      </div>
      </div>
      <div className="form-group">
        <div className="col-sm-offset-2 col-sm-10"></div>
            <button type="submit" className="btn btn-info">Dodaj akord</button>
        </div>
        </div>
    </form>

  );
};

export default ChordsForm;
