import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const ChordCard = ({ chord }) => {
  return (
    <Card style={{ width: '18rem' }}>
      {/* Tutaj umieść obrazek akordu, nazwę itp. */}
      <Card.Body>
        <Card.Title>{chord.name}</Card.Title>
        {/* Dodaj obrazek akordu */}
        <Card.Img variant="top" src={chord.imageUrl} />
        <Card.Text>
          {/* Dodaj dodatkowe informacje */}
        </Card.Text>
        <Button variant="primary">Odsłuchaj</Button>
      </Card.Body>
    </Card>
  );
};

export default ChordCard;
