import React, { useState, useEffect } from 'react';
import ChordCard from './ChordCard';
import { Card, CardGroup } from 'react-bootstrap';
import axios from 'axios'; // Jeśli używasz API do pobierania danych
import './Styles.css';

const Chords = () => {
  const [chords, setChords] = useState([]);

  useEffect(() => {
    // Pobierz akordy z serwera (jeśli korzystasz z API)
    // Przykładowe użycie axios:
    // axios.get('http://localhost:3000/chords')
    //   .then(response => {
    //     setChords(response.data);
    //   })
    //   .catch(error => {
    //     console.error('Błąd pobierania danych:', error);
    //   });

    // Przykładowe dane, usuń to, gdy użyjesz prawdziwego API
    const mockChords = [
      {
        id: 1,
        name: 'C Dur',
        imageUrl: 'link-do-obrazka-c',
        soundUrl: 'link-do-dzwieku-c',
      },
      // ... Dodaj inne akordy
    ];
    setChords(mockChords);
  }, []);



return (
    <div className="chords-container">
        <h1>Akordy i Tabulatury</h1>
            <CardGroup>
                {chords.map((chord) => (
                    <Card key={chord.id} className="custom-card">
                        <Card.Img variant="top" src={chord.imageUrl} />
                            <Card.Body>
                                <Card.Title className="custom-card-title">{chord.name}</Card.Title>
                                <Card.Text>Opis akordu, jeśli jest dostępny</Card.Text>
                            </Card.Body>
                            <Card.Footer>
                            <small className="text-muted">
                              <audio controls>
                                <source src={chord.soundUrl} type="audio/mpeg" />
                                Your browser does not support the audio element.
                              </audio>
                            </small>
                          </Card.Footer>
                        </Card>
      ))}

            </CardGroup>
            </div>
  );
};

export default Chords;
